<template>
  <section class="fit-height">
    <v-row>
      <v-col
        :cols="4"
      >
        <music-controler
          :prev-button="true"
          :next-button="true"
          :detail="getCurrenMusic"
          :src="getCurrenMusic.music"
          :img="getCurrenMusic.image"
          @onPrev="onPrev"
          @onNext="onNext"
          @onEnded="onEnded"
        />
      </v-col>
      <v-col
        :cols="8"
      >
        <Setting />
        <List
          :music-list="musicList"
          @setMusicList="setMusicList"
          @onChangeCurrentIndex="onChangeCurrentIndex"
        />
      </v-col>
    </v-row>
  </section>
</template>

<script lang="ts">
import { Component, Vue } from 'vue-property-decorator'
import MusicControler from '@/components/MusicControl/index.vue'
import { MusicInterface } from '@/components/MusicControl/types/index'
import Setting from './components/setting.vue'
import List from './components/list.vue'

@Component({
  name: 'BusinessMusicList',
  components: {
    MusicControler,
    Setting,
    List
  }
})
export default class extends Vue {
  private musicList : Array<MusicInterface> = [
    { title: 'Test Music 1', author: 'Unknown', music: 'https://file-examples.com/wp-content/uploads/2017/11/file_example_MP3_1MG.mp3', image: 'https://cdn.vuetifyjs.com/images/cards/store.jpg' },
    { title: 'Test Music 2', author: 'Unknown', music: 'https://www.bensound.com/bensound-music/bensound-summer.mp3', image: 'data:image/jpeg;base64,' }

  ]
  private musicIndex = 0

  public get getCurrenMusic() : MusicInterface {
    if (this.musicIndex > this.musicList.length - 1) {
      this.musicIndex = 0
    }

    if (this.musicIndex < 0) {
      this.musicIndex = this.musicList.length - 1
    }

    return this.musicList[this.musicIndex]
  }

  public setMusicList(v: Array<MusicInterface>) {
    this.musicList = v
  }

  public onChangeCurrentIndex(index: number) {
    console.log(index)

    this.musicIndex = index
  }

  /**
   * onPrev
   */
  public onPrev() {
    this.musicIndex--
  }

  /**
   * onNext
   */
  public onNext() {
    this.musicIndex++
  }

  /**
   * onEnded
   */
  public onEnded() {
    this.onNext()
  }
}
</script>

<style lang="scss" scoped>
.fit-height {
  height: 80v;
}
</style>
